var possibleLife = flow.get("player.life") + msg.payload;

if (possibleLife < 0){
    flow.set("player.life", 0);
    return;
}
flow.set("player.life", possibleLife);
return;

var newInMagazine = flow.get("pistol.inMagazine") - 1;
flow.set("pistol.inMagazine", newInMagazine);
return msg;

var possibleNewQuantity = flow.get("pistol.outOfMagazine") + msg.payload;

if (possibleNewQuantity > env.get("MAX_CAPACITY")){
    flow.set("pistol.outOfMagazine", env.get("MAX_CAPACITY"));
    return;
}
else {
    flow.set("pistol.outOfMagazine", possibleNewQuantity);
}
return msg;

var maxPerMagazine = env.get("MAX_PER_MAGAZINE");
var inMagazine = flow.get("pistol.inMagazine");
var outOfMagazine = flow.get("pistol.outOfMagazine");

var freeSpace = maxPerMagazine - inMagazine;

if (freeSpace === 0 || outOfMagazine === 0){
    // Do nothing in this case.
    return msg;
}
else if (freeSpace <= outOfMagazine){
    inMagazine = inMagazine + freeSpace;
    outOfMagazine = outOfMagazine - freeSpace;
}
else if (freeSpace > outOfMagazine){
    inMagazine = inMagazine + outOfMagazine;
    outOfMagazine = 0;
}
flow.set("pistol.inMagazine", inMagazine);
flow.set("pistol.outOfMagazine", outOfMagazine);
return msg;

var msg1 = { payload : flow.get("player")};
var msg2 = { payload : flow.get("pistol")};
return [ [ msg1, msg2 ] ];

var bulletsOutOfMagazine = flow.get("weapon.bulletsOutOfMagazine");

flow.set("weapon.bulletsOutOfMagazine", bulletsOutOfMagazine += msg.payload);



return msg;

var bulletsOutOfMagazine = flow.get("weapon.bulletsOutOfMagazine");
var bulletsInMagazine = flow.get("weapon.bulletsInMagazine");
var maxPerMagazine = env.get("MAX_PER_MAGAZINE");
var difference = maxPerMagazine - bulletsInMagazine;

if (bulletsOutOfMagazine >= difference){
    bulletsInMagazine += difference;
    bulletsOutOfMagazine -= difference;
}
else if (bulletsOutOfMagazine < difference){
    bulletsInMagazine += bulletsOutOfMagazine;
    bulletsOutOfMagazine = 0;
}

flow.set("weapon.bulletsOutOfMagazine", bulletsOutOfMagazine);
flow.set("weapon.bulletsInMagazine", bulletsInMagazine);

return;

var bulletsInMagazine = flow.get("weapon.bulletsInMagazine");

bulletsInMagazine -= msg.payload;

flow.set("weapon.bulletsInMagazine", bulletsInMagazine)

return;

var bulletsInMagazine = flow.get("weapon.bulletsInMagazine");

bulletsInMagazine -- ;

flow.set("weapon.bulletsInMagazine", bulletsInMagazine)

return msg;

var weaponType = env.get("WEAPON_TYPE");
var selectorPosition = flow.get("weapon.selectorPosition");

switch(weaponType) {
    case "semi":
        return msg;
    case "burst":
        if (selectorPosition == "semi") {
            selectorPosition = "burst";
        }
        else {
            selectorPosition = "semi";
        }
        break;
    case "auto":
        if (selectorPosition == "semi") {
            selectorPosition = "burst";
        }
        else if (selectorPosition == "burst") {
            selectorPosition = "auto";
        }
        else {
            selectorPosition = "semi";
        }
        break;
}
flow.set("weapon.selectorPosition", selectorPosition);

return msg;

//Init counter in Setup tab

var selectorPosition = flow.get("weapon.selectorPosition");
var counter = context.get("counter");

if (typeof msg.reset != "undefined") {  //Reset counter also if release trigger for example
    counter = 0;
    context.set("counter", counter);
    msg.reset = {};
    return msg;
}

if (selectorPosition == "burst"){
    var burstRounds = env.get("BURST_ROUNDS");

    if (counter >= burstRounds){
        counter = 0;
        context.set("counter", counter);
        msg.reset = {};
        return msg;
    }
    else {
        counter ++;
        context.set("counter", counter);
    }
}
return;

var roundsPerMinute = env.get("ROUNDS_PER_MINUTE");

msg.delay = 1 / ((roundsPerMinute / 60) / 1000); //We must transform to milliseconds
msg.reset = "";

return msg;

if (msg.payload == 1) msg.payload = 0;
else if (msg.payload == 0) msg.payload = 1;

return msg;
