var possibleLife = flow.get("player.life") + msg.payload;

if (possibleLife < 0){
    flow.set("player.life", 0);
    return;
}
flow.set("player.life", possibleLife);
return;

var newInMagazine = flow.get("pistol.inMagazine") - 1;
flow.set("pistol.inMagazine", newInMagazine);
return msg;

var possibleNewQuantity = flow.get("pistol.outOfMagazine") + msg.payload;

if (possibleNewQuantity > env.get("MAX_CAPACITY")){
    flow.set("pistol.outOfMagazine", env.get("MAX_CAPACITY"));
    return;
}
else {
    flow.set("pistol.outOfMagazine", possibleNewQuantity);
}
return msg;

var maxPerMagazine = env.get("MAX_PER_MAGAZINE");
var inMagazine = flow.get("pistol.inMagazine");
var outOfMagazine = flow.get("pistol.outOfMagazine");

var freeSpace = maxPerMagazine - inMagazine;

if (freeSpace === 0 || outOfMagazine === 0){
    // Do nothing in this case.
    return msg;
}
else if (freeSpace <= outOfMagazine){
    inMagazine = inMagazine + freeSpace;
    outOfMagazine = outOfMagazine - freeSpace;
}
else if (freeSpace > outOfMagazine){
    inMagazine = inMagazine + outOfMagazine;
    outOfMagazine = 0;
}
flow.set("pistol.inMagazine", inMagazine);
flow.set("pistol.outOfMagazine", outOfMagazine);
return msg;

var msg1 = { payload : flow.get("player")};
var msg2 = { payload : flow.get("pistol")};
return [ [ msg1, msg2 ] ];
